void CMfcHarnessDlg::OnBnClickedBrowseDll()
{
    CFileDialog dlg(TRUE, L"dll", nullptr, OFN_FILEMUSTEXIST, L"DLL Files (*.dll)|*.dll|All Files (*.*)|*.*||", this);
    if (dlg.DoModal() == IDOK)
    {
        m_selectedDllPath = dlg.GetPathName();
        SetDlgItemText(IDC_EDIT_DLL_PATH, m_selectedDllPath);
        AppendEvent(L"已选择 DLL：" + m_selectedDllPath);
    }
}

void CMfcHarnessDlg::OnBnClickedRefreshProcesses()
{
    RefreshProcessList();
}

void CMfcHarnessDlg::RefreshProcessList()
{
    m_processes.clear();
    m_processList.DeleteAllItems();

    HANDLE snapshot = ::CreateToolhelp32Snapshot(TH32CS_SNAPPROCESS, 0);
    if (snapshot == INVALID_HANDLE_VALUE)
    {
        AppendEvent(L"无法创建进程快照。");
        return;
    }

    PROCESSENTRY32W entry{ sizeof(PROCESSENTRY32W) };
    if (!::Process32FirstW(snapshot, &entry))
    {
        ::CloseHandle(snapshot);
        AppendEvent(L"Process32First 失败。");
        return;
    }

    const DWORD currentPid = ::GetCurrentProcessId();
    int rowIndex = 0;
    do
    {
        ProcessRow row;
        row.pid = entry.th32ProcessID;
        row.name = entry.szExeFile;
        row.architecture = DescribeArchitecture(entry.th32ProcessID);
        row.injectable = (entry.th32ProcessID != currentPid) && CanInject(entry.th32ProcessID);
        m_processes.emplace_back(row);

        const int idx = m_processList.InsertItem(rowIndex, std::to_wstring(row.pid).c_str());
        m_processList.SetItemText(idx, 1, row.name);
        m_processList.SetItemText(idx, 2, row.architecture);
        m_processList.SetItemText(idx, 3, row.injectable ? L"Yes" : L"No");
        if (!row.injectable)
        {
            m_processList.SetItemState(idx, LVIS_CUT, LVIS_CUT);
        }
        ++rowIndex;
    } while (::Process32NextW(snapshot, &entry));

    ::CloseHandle(snapshot);
    CString summary;
    summary.Format(L"已枚举 %zu 个进程。", m_processes.size());
    AppendEvent(summary);
}

CString CMfcHarnessDlg::DescribeArchitecture(DWORD pid) const
{
    HANDLE processHandle = ::OpenProcess(PROCESS_QUERY_INFORMATION | PROCESS_VM_READ, FALSE, pid);
    if (!processHandle)
    {
        return L"?";
    }

    BOOL isWow64 = FALSE;
    auto fnIsWow64 = reinterpret_cast<BOOL (WINAPI *)(HANDLE, PBOOL)>(::GetProcAddress(::GetModuleHandle(L"kernel32.dll"), "IsWow64Process"));
    CString arch = L"x64";
#ifdef _WIN64
    arch = L"x64";
#else
    arch = L"x86";
#endif
    if (fnIsWow64 && fnIsWow64(processHandle, &isWow64))
    {
#ifdef _WIN64
        arch = isWow64 ? L"x86" : L"x64";
#else
        arch = isWow64 ? L"x86" : L"?";
#endif
    }
    ::CloseHandle(processHandle);
    return arch;
}

bool CMfcHarnessDlg::CanInject(DWORD pid) const
{
    HANDLE processHandle = ::OpenProcess(PROCESS_QUERY_LIMITED_INFORMATION | PROCESS_CREATE_THREAD | PROCESS_VM_OPERATION | PROCESS_VM_WRITE, FALSE, pid);
    if (!processHandle)
    {
        return false;
    }
    ::CloseHandle(processHandle);
    return true;
}

void CMfcHarnessDlg::OnBnClickedInject()
{
    if (m_selectedDllPath.IsEmpty())
    {
        AfxMessageBox(L"请先选择 DLL。", MB_ICONINFORMATION);
        return;
    }

    int selectedIndex = m_processList.GetNextItem(-1, LVNI_SELECTED);
    if (selectedIndex == -1 || selectedIndex >= static_cast<int>(m_processes.size()))
    {
        AfxMessageBox(L"请选择一个可注入的进程。", MB_ICONINFORMATION);
        return;
    }

    const ProcessRow& row = m_processes[selectedIndex];
    if (!row.injectable)
    {
        AfxMessageBox(L"所选进程无法注入 (缺少权限)。", MB_ICONWARNING);
        return;
    }

    CString msg;
    msg.Format(L"[TODO] 将 DLL 注入 PID %lu (%s)。", row.pid, row.name.GetString());
    AppendEvent(msg);
}

void CMfcHarnessDlg::OnBnClickedKillSwitch()
{
    AppendEvent(L"[TODO] 触发 Kill switch 以回滚 hook。", );
