static const ProcessInfo* FindFilteredProcess(const std::vector<ProcessInfo>& processes, const CString& filter, int visibleIndex)
{
    int current = -1;
    for (const auto& row : processes)
    {
        if (!MatchesFilter(row, filter))
        {
            continue;
        }
        ++current;
        if (current == visibleIndex)
        {
            return &row;
        }
    }
    return nullptr;
}

